import { Arg, Args, Mutation, Query, Info, ID, Ctx, Resolver, Root, FieldResolver, Int } from 'type-graphql';
import cleanDeep from 'clean-deep';

import { Week } from '../models/Week';
import { WeekCreateInput } from '../inputs/WeekCreateInput';
import { WeekEditInput } from '../inputs/WeekEditInput';
import { WeekSearchInput } from '../inputs/WeekSearchInput';
import { WeekSearchOrderInput } from '../inputs/WeekSearchOrderInput';
import { getFindOptions } from '../../shared/get-find-options';
import { EntityId, EntityIdScalar } from '../../shared/EntityId';
import { IRequestContext } from '../../shared/IRequestContext';
